generated.css:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

*::after,
*::before {
    box-sizing: border-box;
}
a{
    color:inherit;text-decoration: none;
}
img {
max-width: 100%;
    max-height: 100%;
   
}

ul,
ol {
    list-style: none;
}

body {
    background: #EEE;
  
}

.globalHeader {
    margin: 20px;
}
@media (min-width: 500px){
    .globalHeader{
        margin: 60px 0   100px ;
    }
}
.searchForm{
display: flex;
justify-content: space-between;
}
@media (min-width: 500px){
    .searchForm{
        max-width: 400px;
        margin-left: auto;
        margin-right: auto;
    }
}
.searchForm>input {
    
    width:100%;
    margin-right:10px;
        height: 45px;
    padding: 0 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
}

.searchForm>button {
    white-space: nowrap;
    width: 105px;
    height: 45px;
    border: none;
    border-radius: 4px;
    background: #2F62E3;
    color: white;
    font-size: 19px;
}
.globalMain{
    max-width: 900px;
    margin-left: auto;
    margin-right: auto;

}

.siteList {
    display: flex;
    flex-wrap: wrap;
    margin:20px;
    justify-content: space-between;

}
@media (min-width:500px){
    .siteList{
        margin-left: 0;
        margin-right: -25px;
justify-content: flex-start;
        
    }
}

.siteList>li { 
    margin-bottom: 10px;
    }
@media (min-width:500px){
    .siteList>li {
        margin-right: 25px;
}
}

.siteList .site {
    width: 160px;
        height: 160px;
        background: #FFFFFF;
    display: flex;
    justify-content: center;
    flex-direction: column;
align-items: center;
border: 1px solid #FFFFFF;
border-radius: 4px;
padding: 20px 0;
position: relative;
cursor: pointer;
}

.siteList .site>.logo {
    font-size: 96px;
    width: 65px;
    height: 116px;
    display: flex;
    justify-content: center;
        align-items: center;
        text-transform: uppercase;
}

.siteList .site>.link {
    font-size: 18px;
}
.siteList .site>.close{
    position:absolute;
    top: 0px;
    right: 0px;
    display: none;
    cursor: default;
}
.siteList .site:hover>.close{
    display: block;
}


.siteList  .last >.addButton {
width: 160px;
    height: 160px;
    background: #FFFFFF;
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
    border: 1px solid #FFFFFF;
    border-radius: 4px;
    padding: 20px 0;
}
.siteList .addButton> .icon{
    font-size: 96px;
        width: 100px;
        height: 100px;
        display: flex;
        justify-content: center;
        align-items: center;
}
